SELECT POST_DECLARATION_ID DECLARATION_ID, POST_DECLARATION_DATE DECLARATION_DATE, POST_DECLARATION_CONTENT DECLARATION_CONTENT, USER_ID, POST_DECLARATION_CHECK DECLARATION_CHECK, POST_ID, POST_CATE CATE, POST_PROCESS_DATE DECL_PROCESS_DATE FROM (SELECT ROWNUM AS RNUM, POST_DECLARATION_ID, POST_DECLARATION_DATE, POST_DECLARATION_CONTENT, USER_ID, POST_DECLARATION_CHECK, POST_ID, POST_CATE, POST_PROCESS_DATE FROM (SELECT POST_DECLARATION_ID, POST_DECLARATION_DATE, POST_DECLARATION_CONTENT, USER_ID, POST_DECLARATION_CHECK, POST_ID, POST_CATE, POST_PROCESS_DATE FROM TBL_POST_DECLARATION
POST_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'
AND TO_CHAR(POST_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}
AND TO_CHAR(POST_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}
AND POST_CATE = #{selectDecl.declarationType}
AND POST_DECLARATION_CHECK = #{selectDecl.processType}
ORDER BY POST_DECLARATION_ID)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT count(POST_DECLARATION_ID)
FROM TBL_POST_DECLARATION


        POST_DECLARATION_CONTENT LIKE '%' || #{selectDecl.declarationContent} || '%'


        AND TO_CHAR(POST_DECLARATION_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startDeclarationDate} AND #{selectDecl.endDeclarationDate}


        AND TO_CHAR(POST_PROCESS_DATE, 'YYYY-MM-DD') BETWEEN
        #{selectDecl.startPocecssDate} AND #{selectDecl.endPocecssDate}


        AND POST_CATE = #{selectDecl.declarationType}


        AND POST_DECLARATION_CHECK = #{selectDecl.processType}
